<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{	protected $title;		// заголовок страницы	protected $content;		// содержание страницы	protected $sidebar;		// содержание страницы	public $user;	//	// Конструктор.	//	function __construct()	{	}	protected function before()	{		$this->title = 'Блог';		$this->content = '';		$this->sidebar = $this->Template('view/v_right.php');		$mUsers = M_Users::Instance();		$this->user = $mUsers->Get();	}	//	// Генерация базового шаблонаы	//	public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content, 'sidebar' => $this->sidebar, 'user' => $this->user);		$page = $this->Template('view/v_main.php', $vars);		echo $page;	}}