<?phpfunction __autoload($classname){	switch ($classname[0]) {		case 'C':			include_once("controller/$classname.php");			break;		case 'M':			include_once("model/$classname.php");			break;	}}$action = 'action_';$action .= (isset($_GET['act'])) ? $_GET['act'] : 'all';switch ($_GET['c']){	case 'login':		$controller = new C_login();		break;	case 'editor':		$controller = new C_Editor();		break;	default:		$controller = new C_Articles();}// Открытие сессии.session_start();$controller->Request($action);