<?php	include_once('startup.php');	include_once('model.php');	$link = startup();	$data = $_GET;	$id = $data['id'];	$article = articles_get($link, $id);// Обработка отправки формы.if (!empty($_POST)){	if (articles_edit($link, $_POST['title'], $_POST['content'], $article['id_article']))	{		header('Location: editor.php');		die();	}	$title = $_POST['title'];	$content = $_POST['content'];	$error = true;}else{	$title = $article['title'];	$content = $article['content'];	$error = false;}	include('theme/edit.php');?>